package challenges

import (
	"strings"
)

func TuningTrouble(distinct int, input string) int {
	parts := strings.Split(input, "")

	// character to position
	markers := make(map[string]int)
	var max int
	for i, letter := range parts {
		_, ok := markers[letter]
		if ok {
			// Start over
			markers = make(map[string]int)
		}

		markers[letter] = i
		if len(markers) == distinct {
			max = i + 1
			break
		}
	}

	return max
}

var (
	TuningTroubleInput = ``
)
